import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();
  const decodedProjectName = decodeURIComponent(projectName);

  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [previewDoc, setPreviewDoc] = useState(null);
  const [file, setFile] = useState(null);

  // Fetch project data
  useEffect(() => {
    fetch(`http://localhost:2000/api/projects/detail/${decodedProjectName}`)
      .then((res) => res.json())
      .then((data) => {
        setProject(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("❌ Error fetching project:", err);
        setLoading(false);
      });
  }, [decodedProjectName]);

  // Save project back to MongoDB
  const handleSave = async () => {
    await fetch(`http://localhost:2000/api/projects/detail/${decodedProjectName}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(project),
    });
    alert("✅ Saved to database");
  };

  // Upload document (local preview only)
  const handleFileUpload = () => {
    if (!file) return;
    const newDoc = {
      name: file.name,
      url: URL.createObjectURL(file),
      fileType: file.type,
    };
    setProject((prev) => ({ ...prev, documents: [...(prev.documents || []), newDoc] }));
    setFile(null);
  };

  const handleDeleteDoc = (index) => {
    const updatedDocs = project.documents.filter((_, i) => i !== index);
    setProject((prev) => ({ ...prev, documents: updatedDocs }));
    if (previewDoc && project.documents[index]?.name === previewDoc.name) {
      setPreviewDoc(null);
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!project) return <p>Project not found</p>;

  return (
    <>
      <button onClick={() => navigate(-1)} className="back-button">← Back</button>
      <h1 className="header">{project.name}</h1>

      {/* Client Info */}
      <section>
        <h2>Client Info</h2>
        <input
          type="text"
          value={project.clientInfo?.name || ""}
          onChange={(e) => setProject({ ...project, clientInfo: { ...project.clientInfo, name: e.target.value } })}
          placeholder="Client Name"
        />
        <input
          type="text"
          value={project.clientInfo?.contact || ""}
          onChange={(e) => setProject({ ...project, clientInfo: { ...project.clientInfo, contact: e.target.value } })}
          placeholder="Contact Person"
        />
      </section>

      {/* Meta Info */}
      <section>
        <h2>Meta Info</h2>
        <input
          type="text"
          value={project.metaInfo?.duration || ""}
          onChange={(e) => setProject({ ...project, metaInfo: { ...project.metaInfo, duration: e.target.value } })}
          placeholder="Duration"
        />
        <input
          type="text"
          value={project.metaInfo?.team || ""}
          onChange={(e) => setProject({ ...project, metaInfo: { ...project.metaInfo, team: e.target.value } })}
          placeholder="Team"
        />
      </section>

      {/* Engagement */}
      <section>
        <h2>Engagement</h2>
        <input
          type="text"
          value={project.engagementInfo?.scope || ""}
          onChange={(e) => setProject({ ...project, engagementInfo: { ...project.engagementInfo, scope: e.target.value } })}
          placeholder="Scope"
        />
        <input
          type="text"
          value={project.engagementInfo?.objectives || ""}
          onChange={(e) => setProject({ ...project, engagementInfo: { ...project.engagementInfo, objectives: e.target.value } })}
          placeholder="Objectives"
        />
        <input
          type="text"
          value={project.engagementInfo?.risks || ""}
          onChange={(e) => setProject({ ...project, engagementInfo: { ...project.engagementInfo, risks: e.target.value } })}
          placeholder="Risks"
        />
      </section>

      {/* Documents */}
      <section>
        <h2>Documents</h2>
        <ul>
          {project.documents?.map((doc, index) => (
            <li key={index}>
              <span
                onClick={() => setPreviewDoc(doc)}
                style={{ cursor: "pointer", color: "#1a3a6b" }}
              >
                {doc.name}
              </span>
              <button onClick={() => handleDeleteDoc(index)} style={{ marginLeft: "10px", color: "red" }}>
                Delete
              </button>
            </li>
          ))}
        </ul>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <button onClick={handleFileUpload}>Upload</button>

        {/* Preview */}
        {previewDoc && (
          <div className="preview-section">
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <h4>Preview: {previewDoc.name}</h4>
              <button onClick={() => setPreviewDoc(null)}>✖</button>
            </div>

            {previewDoc.name.endsWith(".pdf") ? (
              <embed src={previewDoc.url} type="application/pdf" width="100%" height="500px" />
            ) : previewDoc.name.match(/\.(jpg|jpeg|png|gif)$/i) ? (
              <img src={previewDoc.url} alt={previewDoc.name} style={{ maxWidth: "100%" }} />
            ) : previewDoc.name.match(/\.(doc|docx|xls|xlsx|ppt|pptx)$/i) ? (
              <iframe
                src={`https://view.officeapps.live.com/op/embed.aspx?src=${encodeURIComponent(previewDoc.url)}`}
                width="100%"
                height="500px"
                title="Office Preview"
              ></iframe>
            ) : (
              <iframe src={previewDoc.url} width="100%" height="400px" title="preview"></iframe>
            )}
          </div>
        )}
      </section>

      <button onClick={handleSave} style={{ marginTop: "20px" }}>💾 Save</button>
    </>
  );
};

export default ProjectDetail;
