// src/components/ProjectDetail.js
import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import "./toast.css"; // we'll add basic toast styles

const API_BASE = "http://localhost:2000/api/project-detail";

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();
  const decodedProjectName = decodeURIComponent(projectName);

  const [expanded, setExpanded] = useState({
    client: false,
    meta: false,
    engagement: false,
    documentation: false,
    history: false,
  });

  const [projectData, setProjectData] = useState(null);
  const [editMode, setEditMode] = useState({});
  const [file, setFile] = useState(null);
  const [previewDoc, setPreviewDoc] = useState(null);
  const [isEdited, setIsEdited] = useState(false);
  const [toast, setToast] = useState("");
  const [searchTerm, setSearchTerm] = useState("");

  // Simulated role
  const role = "manager"; // viewer | editor | manager

  useEffect(() => {
    fetch(`${API_BASE}/${decodedProjectName}`)
      .then((res) => res.json())
      .then((data) => setProjectData(data))
      .catch(() =>
        setProjectData({
          projectName: decodedProjectName,
          clientInfo: {},
          metaInfo: {},
          engagementInfo: {},
          documents: [],
          history: [],
        })
      );
  }, [decodedProjectName]);

  const toggleSection = (section) => {
    setExpanded((prev) => ({ ...prev, [section]: !prev[section] }));
  };

  // Save function
  const saveData = async () => {
    if (!projectData) return;
    const res = await fetch(`${API_BASE}/${decodedProjectName}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(projectData),
    });
    if (res.ok) {
      const updated = await res.json();
      setProjectData(updated);
      setIsEdited(false);
      showToast("‚úÖ Saved successfully!");
    } else {
      showToast("‚ùå Failed to save");
    }
  };

  // Autosave after 2s delay
  useEffect(() => {
    if (isEdited) {
      const timer = setTimeout(() => {
        saveData();
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [projectData]);

  const showToast = (msg) => {
    setToast(msg);
    setTimeout(() => setToast(""), 3000);
  };

  const handleInputChange = (section, field, value) => {
    setProjectData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: value },
      history: [
        ...(prev.history || []),
        { section, field, value, date: new Date().toLocaleString() },
      ],
    }));
    setIsEdited(true);
  };

  const handleFileUpload = () => {
    if (!file) return;
    const newDoc = {
      name: file.name,
      url: URL.createObjectURL(file),
      size: `${(file.size / 1024).toFixed(1)} KB`,
      uploaded: new Date().toLocaleString(),
    };
    setProjectData((prev) => ({
      ...prev,
      documents: [...(prev.documents || []), newDoc],
    }));
    setFile(null);
    setIsEdited(true);
  };

  const handleDeleteDoc = (index) => {
    setProjectData((prev) => ({
      ...prev,
      documents: prev.documents.filter((_, i) => i !== index),
    }));
    if (previewDoc && projectData.documents[index]?.name === previewDoc.name) {
      setPreviewDoc(null);
    }
    setIsEdited(true);
  };

  // Progress bar calculation
  const calcProgress = () => {
    if (!projectData) return 0;
    let filled = 0;
    let total = 7;
    if (projectData.clientInfo?.name) filled++;
    if (projectData.clientInfo?.contact) filled++;
    if (projectData.clientInfo?.address) filled++;
    if (projectData.metaInfo?.duration) filled++;
    if (projectData.metaInfo?.team) filled++;
    if (projectData.engagementInfo?.scope) filled++;
    if (projectData.engagementInfo?.objectives) filled++;
    return Math.round((filled / total) * 100);
  };

  if (!projectData) return <p>Loading...</p>;

  return (
    <>
      <button onClick={() => navigate(-1)} className="back-button">‚Üê Back</button>
      <h1 className="header">{decodedProjectName}</h1>

      {/* Progress bar */}
      <div className="progress-bar">
        <div className="progress" style={{ width: `${calcProgress()}%` }}></div>
      </div>
      <p>{calcProgress()}% Completed</p>

      <div className="project-detail-wrapper">
        {/* Client Information */}
        <section>
          <h2 onClick={() => toggleSection("client")} className="collapsible-header">
            <span className={`arrow ${expanded.client ? "down" : ""}`}>‚ñ∂</span> Client Information
          </h2>
          {expanded.client && (
            <div className="collapsible-content">
              {role === "viewer" ? (
                <ul>
                  <li><strong>Name:</strong> {projectData.clientInfo?.name}</li>
                  <li><strong>Contact:</strong> {projectData.clientInfo?.contact}</li>
                  <li><strong>Address:</strong> {projectData.clientInfo?.address}</li>
                </ul>
              ) : (
                <>
                  <input type="text" value={projectData.clientInfo?.name || ""} onChange={(e) => handleInputChange("clientInfo", "name", e.target.value)} placeholder="Client Name" />
                  <input type="text" value={projectData.clientInfo?.contact || ""} onChange={(e) => handleInputChange("clientInfo", "contact", e.target.value)} placeholder="Contact Person" />
                  <input type="text" value={projectData.clientInfo?.address || ""} onChange={(e) => handleInputChange("clientInfo", "address", e.target.value)} placeholder="Address" />
                </>
              )}
            </div>
          )}
        </section>

        {/* Meta Information */}
        <section>
          <h2 onClick={() => toggleSection("meta")} className="collapsible-header">
            <span className={`arrow ${expanded.meta ? "down" : ""}`}>‚ñ∂</span> Meta Information
          </h2>
          {expanded.meta && (
            <div className="collapsible-content">
              <input type="text" value={projectData.metaInfo?.duration || ""} onChange={(e) => handleInputChange("metaInfo", "duration", e.target.value)} placeholder="Audit Duration" />
              <input type="text" value={projectData.metaInfo?.team || ""} onChange={(e) => handleInputChange("metaInfo", "team", e.target.value)} placeholder="Team Members" />
            </div>
          )}
        </section>

        {/* Engagement */}
        <section>
          <h2 onClick={() => toggleSection("engagement")} className="collapsible-header">
            <span className={`arrow ${expanded.engagement ? "down" : ""}`}>‚ñ∂</span> Audit Engagement
          </h2>
          {expanded.engagement && (
            <div className="collapsible-content">
              <input type="text" value={projectData.engagementInfo?.scope || ""} onChange={(e) => handleInputChange("engagementInfo", "scope", e.target.value)} placeholder="Scope" />
              <input type="text" value={projectData.engagementInfo?.objectives || ""} onChange={(e) => handleInputChange("engagementInfo", "objectives", e.target.value)} placeholder="Objectives" />
              <input type="text" value={projectData.engagementInfo?.risks || ""} onChange={(e) => handleInputChange("engagementInfo", "risks", e.target.value)} placeholder="Risks" />
            </div>
          )}
        </section>

        {/* Documentation */}
        <section>
          <h2 onClick={() => toggleSection("documentation")} className="collapsible-header">
            <span className={`arrow ${expanded.documentation ? "down" : ""}`}>‚ñ∂</span> Documentation
          </h2>
          {expanded.documentation && (
            <div className="collapsible-content">
              <input type="text" placeholder="Search documents..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
              <ul>
                {projectData.documents
                  ?.filter((doc) => doc.name.toLowerCase().includes(searchTerm.toLowerCase()))
                  .map((doc, index) => (
                  <li key={index}>
                    <span onClick={() => setPreviewDoc(doc)}>{doc.name}</span>
                    <small> ({doc.size}, {doc.uploaded})</small>
                    <button onClick={() => handleDeleteDoc(index)} style={{ color: "red" }}>Delete</button>
                  </li>
                ))}
              </ul>
              <input type="file" onChange={(e) => setFile(e.target.files[0])} multiple />
              <button onClick={handleFileUpload}>Upload</button>

              {previewDoc && (
                <div>
                  <h4>Preview: {previewDoc.name}</h4>
                  <button onClick={() => setPreviewDoc(null)}>‚úñ Close</button>
                  {previewDoc.name.match(/\.pdf$/i) ? (
                    <embed src={previewDoc.url} type="application/pdf" width="100%" height="400px" />
                  ) : previewDoc.name.match(/\.(jpg|jpeg|png)$/i) ? (
                    <img src={previewDoc.url} alt={previewDoc.name} />
                  ) : (
                    <iframe src={previewDoc.url} width="100%" height="400px"></iframe>
                  )}
                </div>
              )}
            </div>
          )}
        </section>

        {/* History */}
        <section>
          <h2 onClick={() => toggleSection("history")} className="collapsible-header">
            <span className={`arrow ${expanded.history ? "down" : ""}`}>‚ñ∂</span> Change History
          </h2>
          {expanded.history && (
            <ul>
              {projectData.history?.map((h, i) => (
                <li key={i}>{h.date}: <strong>{h.section}</strong> ‚Äì {h.field} ‚Üí {h.value}</li>
              ))}
            </ul>
          )}
        </section>

        {isEdited && (
          <button onClick={saveData} className="save-btn">üíæ Save All</button>
        )}
      </div>

      {toast && <div className="toast">{toast}</div>}
    </>
  );
};

export default ProjectDetail;
