import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Dashboard from './components/SideBar/Dashboard';
import Projects from './components/SideBar/Projects';
import RiskManagement from './components/SideBar/RiskManagement';
import Controls from './components/SideBar/Controls';
import Findings from './components/SideBar/Findings';
import Reports from './components/SideBar/Reports';
import Library from './components/SideBar/Library';
import NavBar from './components/NavBar/NavBar';
import LoginPage from './components/LoginPage';
import SideBar from './components/SideBar/SideBar';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    return (
        <Router>
            <div className="App" style={{ display: 'flex' }}>
                {isAuthenticated && <SideBar />}
                <div style={{ flex: 1 }}>
                    {isAuthenticated && <NavBar />}
                    <Routes>
                        <Route
                            path="/"
                            element={
                                isAuthenticated
                                    ? <Navigate to="/dashboard" />
                                    : <LoginPage setIsAuthenticated={setIsAuthenticated} />
                            }
                        />
                        <Route path="/dashboard" element={isAuthenticated ? <Dashboard /> : <Navigate to="/" />} />
                        <Route path="/projects" element={isAuthenticated ? <Projects /> : <Navigate to="/" />} />
                        <Route path="/risk-management" element={isAuthenticated ? <RiskManagement /> : <Navigate to="/" />} />
                        <Route path="/controls" element={isAuthenticated ? <Controls /> : <Navigate to="/" />} />
                        <Route path="/findings" element={isAuthenticated ? <Findings /> : <Navigate to="/" />} />
                        <Route path="/reports" element={isAuthenticated ? <Reports /> : <Navigate to="/" />} />
                        <Route path="/library" element={isAuthenticated ? <Library /> : <Navigate to="/" />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default App;
