import React, { useState } from 'react';
import { FaSearch, FaBell, FaUserCircle } from 'react-icons/fa';

const NavBar = () => {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);

    const toggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);
    };

    const styles = {
        navbar: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            padding: '10px 20px',
            backgroundColor: '#333',
            color: '#fff',
        },
        logo: {
            fontSize: '24px',
            fontWeight: 'bold',
            color: '#fff',
            textDecoration: 'none',
        },
        searchContainer: {
            display: 'flex',
            alignItems: 'center',
            backgroundColor: '#fff',
            borderRadius: '5px',
            padding: '5px 10px',
        },
        searchInput: {
            border: 'none',
            outline: 'none',
            marginRight: '5px',
        },
        searchIcon: {
            color: '#333',
        },
        iconsContainer: {
            display: 'flex',
            alignItems: 'center',
            gap: '15px',
            position: 'relative',
        },
        icon: {
            fontSize: '30px',
            cursor: 'pointer',
        },
        dropdownMenu: {
            position: 'absolute',
            top: '40px',
            right: '0',
            backgroundColor: '#fff',
            color: '#333',
            boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
            borderRadius: '5px',
            padding: '10px',
            display: isDropdownOpen ? 'block' : 'none',
            width: '150px',
        },
        dropdownItem: {
            padding: '5px',
            cursor: 'pointer',
            borderBottom: '1px solid #ddd',
            textAlign: 'center',
        },
        lastItem: {
            padding: '10px',
            cursor: 'pointer',
            textAlign: 'center',
        },
    };

    return (
        <nav style={styles.navbar}>
            <a href="/" style={styles.logo}>
                <img 
                    src="/logo/android-chrome-192x192.png" 
                    alt="Logo" 
                    style={{ height: '40px', width: '40px' }} 
                />
            </a>
            <div style={styles.searchContainer}>
                <input
                    type="text"
                    placeholder="Search here"
                    style={styles.searchInput}
                />
                <FaSearch style={styles.searchIcon} />
            </div>
            <div style={styles.iconsContainer}>
                <FaBell style={styles.icon} title="Notifications" />
                <FaUserCircle style={styles.icon} title="User Profile" onClick={toggleDropdown} />
                <div style={styles.dropdownMenu}>
                    <div style={styles.dropdownItem}>Profile</div>
                    <div style={styles.dropdownItem}>Settings</div>
                    <div style={styles.lastItem}>Sign Out</div>
                </div>
            </div>
        </nav>
    );
};

export default NavBar;
