import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LoginPage = ({ setIsAuthenticated }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();  // Hook for navigation

    const handleSignIn = () => {
        console.log('Sign In clicked', { username, password });
        
        // Authentication logic (replace with actual auth handling)
        if (username && password) {
            setIsAuthenticated(true);  // Update authentication state
            navigate('/dashboard');    // Redirect to dashboard
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#f0f2f5' }}>
            <div style={{ backgroundColor: '#fff', padding: '2rem', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', maxWidth: '400px', textAlign: 'center' }}>
                <h1 style={{ marginBottom: '1.5rem', color: '#333' }}>Welcome Back</h1>
                <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} style={{ width: '100%', padding: '0.75rem', marginBottom: '1rem' }} />
                <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} style={{ width: '100%', padding: '0.75rem', marginBottom: '1.5rem' }} />
                <button onClick={handleSignIn} style={{ width: '100%', padding: '0.75rem', backgroundColor: '#4CAF50', color: '#fff', borderRadius: '50px', cursor: 'pointer' }}>
                    Sign In
                </button>
            </div>
        </div>
    );
};

export default LoginPage;
