// src/components/ProjectDetail.js
import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const API_BASE = "http://localhost:2000/api/projects";

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const [project, setProject] = useState(null);
  const [expanded, setExpanded] = useState({
    client: false,
    meta: false,
    engagement: false,
    documentation: false,
  });
  const [previewDoc, setPreviewDoc] = useState(null);
  const [file, setFile] = useState(null);

  // Fetch project
  useEffect(() => {
    fetch(`${API_BASE}/${projectName}`)
      .then((res) => res.json())
      .then(setProject)
      .catch(console.error);
  }, [projectName]);

  const toggleSection = (section) =>
    setExpanded((prev) => ({ ...prev, [section]: !prev[section] }));

  const handleSave = async () => {
    await fetch(`${API_BASE}/${project._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(project),
    });
    alert("‚úÖ Saved to database");
  };

  const handleFileUpload = async () => {
    if (!file) return;
    const newDoc = {
      name: file.name,
      url: URL.createObjectURL(file), // (In real apps, upload to S3 or GridFS instead!)
      type: file.type,
    };
    const res = await fetch(`${API_BASE}/${project._id}/upload`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newDoc),
    });
    const updated = await res.json();
    setProject(updated);
    setFile(null);
  };

  if (!project) return <p>Loading...</p>;

  return (
    <>
      <button onClick={() => navigate(-1)} className="back-button">
        ‚Üê Back
      </button>
      <h1 className="header">{project.AllProjects}</h1>

      <div className="project-detail-wrapper">
        {/* Client Info */}
        <section>
          <h2 onClick={() => toggleSection("client")} className="collapsible-header">
            <span className={`arrow ${expanded.client ? "down" : ""}`}>‚ñ∂</span> Client Information
          </h2>
          {expanded.client && (
            <div className="collapsible-content">
              <input
                value={project.clientInfo?.name || ""}
                onChange={(e) =>
                  setProject({ ...project, clientInfo: { ...project.clientInfo, name: e.target.value } })
                }
              />
              <input
                value={project.clientInfo?.contact || ""}
                onChange={(e) =>
                  setProject({ ...project, clientInfo: { ...project.clientInfo, contact: e.target.value } })
                }
              />
              <input
                value={project.clientInfo?.address || ""}
                onChange={(e) =>
                  setProject({ ...project, clientInfo: { ...project.clientInfo, address: e.target.value } })
                }
              />
              <input
                value={project.clientInfo?.email || ""}
                onChange={(e) =>
                  setProject({ ...project, clientInfo: { ...project.clientInfo, email: e.target.value } })
                }
              />
              <input
                value={project.clientInfo?.phone || ""}
                onChange={(e) =>
                  setProject({ ...project, clientInfo: { ...project.clientInfo, phone: e.target.value } })
                }
              />
            </div>
          )}
        </section>

        {/* Meta Info */}
        <section>
          <h2 onClick={() => toggleSection("meta")} className="collapsible-header">
            <span className={`arrow ${expanded.meta ? "down" : ""}`}>‚ñ∂</span> Meta Information
          </h2>
          {expanded.meta && (
            <div className="collapsible-content">
              <input
                value={project.metaInfo?.duration || ""}
                onChange={(e) =>
                  setProject({ ...project, metaInfo: { ...project.metaInfo, duration: e.target.value } })
                }
              />
              <input
                value={project.metaInfo?.team || ""}
                onChange={(e) =>
                  setProject({ ...project, metaInfo: { ...project.metaInfo, team: e.target.value } })
                }
              />
            </div>
          )}
        </section>

        {/* Engagement Info */}
        <section>
          <h2 onClick={() => toggleSection("engagement")} className="collapsible-header">
            <span className={`arrow ${expanded.engagement ? "down" : ""}`}>‚ñ∂</span> Audit Engagement
          </h2>
          {expanded.engagement && (
            <div className="collapsible-content">
              <input
                value={project.engagementInfo?.scope || ""}
                onChange={(e) =>
                  setProject({
                    ...project,
                    engagementInfo: { ...project.engagementInfo, scope: e.target.value },
                  })
                }
              />
              <input
                value={project.engagementInfo?.objectives || ""}
                onChange={(e) =>
                  setProject({
                    ...project,
                    engagementInfo: { ...project.engagementInfo, objectives: e.target.value },
                  })
                }
              />
              <input
                value={project.engagementInfo?.risks || ""}
                onChange={(e) =>
                  setProject({
                    ...project,
                    engagementInfo: { ...project.engagementInfo, risks: e.target.value },
                  })
                }
              />
            </div>
          )}
        </section>

        {/* Documentation */}
        <section>
          <h2 onClick={() => toggleSection("documentation")} className="collapsible-header">
            <span className={`arrow ${expanded.documentation ? "down" : ""}`}>‚ñ∂</span> Documentation
          </h2>
          {expanded.documentation && (
            <div className="collapsible-content">
              <ul>
                {project.documents?.map((doc, idx) => (
                  <li key={idx}>
                    <span
                      onClick={() => setPreviewDoc(doc)}
                      style={{ cursor: "pointer", color: "#1a3a6b" }}
                    >
                      {doc.name}
                    </span>
                  </li>
                ))}
              </ul>
              <input type="file" onChange={(e) => setFile(e.target.files[0])} />
              <button onClick={handleFileUpload}>Upload</button>

              {previewDoc && (
                <div>
                  <h4>
                    Preview: {previewDoc.name}
                    <button onClick={() => setPreviewDoc(null)}>‚úñ</button>
                  </h4>
                  {previewDoc.name.endsWith(".pdf") ? (
                    <embed src={previewDoc.url} type="application/pdf" width="100%" height="500px" />
                  ) : previewDoc.name.match(/\.(jpg|jpeg|png|gif)$/i) ? (
                    <img src={previewDoc.url} alt={previewDoc.name} />
                  ) : (
                    // Microsoft 365 Viewer for Word/Excel/PowerPoint
                    <iframe
                      src={`https://view.officeapps.live.com/op/embed.aspx?src=${encodeURIComponent(
                        previewDoc.url
                      )}`}
                      width="100%"
                      height="500px"
                      frameBorder="0"
                      title="doc-preview"
                    ></iframe>
                  )}
                </div>
              )}
            </div>
          )}
        </section>

        <button onClick={handleSave}>üíæ Save All</button>
      </div>
    </>
  );
};

export default ProjectDetail;
