import React, { useState } from 'react';
import CompletedProjects from './CompletedProjects'; // üëà import the new component

const Projects = () => {
  const [projectList, setProjectList] = useState([]);
  const [sectionTitle, setSectionTitle] = useState('');
  const [activeComponent, setActiveComponent] = useState(null); // üëà new state

  const handleClick = async (label) => {
    let endpoint = '';
    let title = '';
    let component = null;

    switch (label) {
      case 'Completed':
        endpoint = 'completed';
        title = 'Audits Completed';
        component = 'completed';
        break;
      case 'In-Progress':
        endpoint = 'in-progress';
        title = 'Audits In Progress';
        component = 'inProgress';
        break;
      case 'Unexecuted':
        endpoint = 'not-completed';
        title = 'Audits Not Completed';
        component = 'notCompleted';
        break;
      case 'Project List':
        endpoint = 'all-projects';
        title = 'All Projects';
        component = 'projectList';
        break;
      default:
        return;
    }

    try {
      const response = await fetch(`http://localhost:2000/api/projects/${endpoint}`);
      const data = await response.json();

      if (label === 'Project List' && data.projects) {
        setProjectList(data.projects);
      } else {
        const key = Object.keys(data)[0];
        setProjectList(data[key] || []);
      }

      setSectionTitle(title);
      setActiveComponent(component); // üëà update component to show
    } catch (error) {
      console.error('‚ùå Error fetching data:', error);
    }
  };

  return (
    <div
      style={{
        padding: '20px',
        fontFamily: 'Arial, sans-serif',
        backgroundColor: '#f4f6f9',
        minHeight: '100vh',
        width: '79.2%',
        marginLeft: 'auto',
        transform: 'translateY(-94%)',
      }}
    >
      <h1 style={{ borderBottom: '1px solid #007BFF', paddingBottom: '5px', color: '#333' }}>
        Projects
      </h1>

      <div
        style={{
          display: 'flex',
          justifyContent: 'space-around',
          marginTop: '40px',
          flexWrap: 'wrap',
          gap: '20px',
        }}
      >
        {['Completed', 'In-Progress', 'Unexecuted', 'Project List'].map((label) => (
          <div
            key={label}
            onClick={() => handleClick(label)}
            style={{
              width: '120px',
              height: '120px',
              borderRadius: '50%',
              backgroundColor: '#007BFF',
              color: '#fff',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              cursor: 'pointer',
              textAlign: 'center',
              fontSize: '14px',
              boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
              transition: 'transform 0.3s ease, box-shadow 0.3s ease',
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'scale(1.1)';
              e.currentTarget.style.boxShadow = '0 6px 10px rgba(0, 0, 0, 0.15)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'scale(1)';
              e.currentTarget.style.boxShadow = '0 4px 6px rgba(0, 0, 0, 0.1)';
            }}
          >
            {label}
          </div>
        ))}
      </div>

      {/* üëá Conditionally render the component below */}
      {projectList.length > 0 && activeComponent === 'projectList' && (
        <CompletedProjects projects={projectList} title={sectionTitle} />
      )}

      {/* Optional: handle other sections the same way in future */}
    </div>
  );
};

export default Projects;
