import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // üëà Import navigate

const Inprogress = () => {
  const [projects, setProjects] = useState([]);
  const navigate = useNavigate(); // üëà Initialize the hook

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const res = await fetch('http://localhost:2000/api/projects/in-progress');
        const data = await res.json();
        if (data.projects) {
          setProjects(data.projects);
        }
      } catch (err) {
        console.error('‚ùå Failed to fetch projects:', err);
      }
    };

    fetchProjects();
  }, []);

  return (
    <div
      style={{
        padding: '20px',
        fontFamily: 'Arial, sans-serif',
        backgroundColor: '#f4f6f9',
        minHeight: '100vh',
        marginLeft: 'auto',
      }}
    >
      {/* ‚úÖ Back Button */}
      <button className='back-button'
        onClick={() => navigate('/projects')}
      >
        ‚Üê Back
      </button>

      <h1 style={{ clear: 'both', borderBottom: '1px solid #007BFF', paddingBottom: '5px', color: '#333' }}>
        In-Progress
      </h1>

      <ul>
        {projects.map((project, index) => (
          <li key={index}>
            {Object.entries(project).map(([key, value]) => (
              <div key={key}>
                <strong>{key}:</strong> {value}
              </div>
            ))}
            <hr />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Inprogress;
