import React from 'react';

const Library = () => {
    const [isBouncing, setIsBouncing] = React.useState(false);

    const handleClick = () => {
        setIsBouncing(true);
        setTimeout(() => setIsBouncing(false), 400); // bounce duration
    };

    // Simple directory/library SVG icon
    const iconStyle = {
        cursor: 'pointer',
        transition: 'transform 0.2s',
        transform: isBouncing ? 'scale(1.2) translateY(-10px)' : 'scale(1) translateY(0)',
        display: 'inline-block',
        marginBottom: '20px'
    };

    const LibraryIcon = (
        <svg
            onClick={handleClick}
            style={iconStyle}
            width="48"
            height="48"
            viewBox="0 0 48 48"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
        >
            <rect x="6" y="14" width="36" height="24" rx="3" fill="#007BFF" />
            <rect x="10" y="10" width="12" height="8" rx="2" fill="#5dade2" />
            <rect x="24" y="10" width="14" height="8" rx="2" fill="#2980b9" />
            <rect x="12" y="22" width="24" height="12" rx="2" fill="#fff" opacity="0.7" />
        </svg>
    );

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f6f9', minHeight: '100vh', width: '79.2%', alignItems: 'center', marginLeft: 'auto', transform: 'translateY(-94%)' }}>
            {/* Header */}
            <h1 style={{ borderBottom: '1px solid #007BFF', paddingBottom: '5px', color: '#333' }}>Library</h1>
            {/* Library_Icon */}
        </div>
    );
};

export default Library;
