import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Inprogress = () => {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const res = await fetch('http://localhost:2000/api/projects/in-progress');
        const data = await res.json();
        if (data.projects) {
          setProjects(data.projects);
        }
      } catch (err) {
        console.error('❌ Failed to fetch projects:', err);
      }
    };

    fetchProjects();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSelectedProject((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleProjectSelect = (project) => {
    setSelectedProject(project);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('🔄 Updated Project:', selectedProject);
    // Optional: POST or PUT to API
  };

  return (
    <div style={{ padding: '2rem' }}>
      <button className='back-button' onClick={() => navigate('/projects')}>
        ← Back
      </button>

      <h1 className='header'>In-Progress Projects</h1>

      {/* Project List */}
      <ul>
        {projects.map((project, index) => (
          <li
            key={index}
            style={{ cursor: 'pointer', marginBottom: '1rem' }}
            onClick={() => handleProjectSelect(project)}
          >
            {project.projectName || 'Unnamed Project'}
            <hr />
          </li>
        ))}
      </ul>

      {/* Project Form */}
      {selectedProject && (
        <form onSubmit={handleSubmit} style={{ marginTop: '2rem' }}>
          <h2>Edit In-Progress Project</h2>

          <label>
            Project ID:
            <input
              type="text"
              name="projectId"
              value={selectedProject.projectId || ''}
              onChange={handleInputChange}
              required
            />
          </label>
          <br />

          <label>
            Project Name:
            <input
              type="text"
              name="projectName"
              value={selectedProject.projectName || ''}
              onChange={handleInputChange}
              required
            />
          </label>
          <br />

          <label>
            Client Name:
            <input
              type="text"
              name="clientName"
              value={selectedProject.clientName || ''}
              onChange={handleInputChange}
            />
          </label>
          <br />

          <label>
            Lead Auditor:
            <input
              type="text"
              name="leadAuditor"
              value={selectedProject.leadAuditor || ''}
              onChange={handleInputChange}
            />
          </label>
          <br />

          <label>
            Start Date:
            <input
              type="date"
              name="startDate"
              value={selectedProject.startDate || ''}
              onChange={handleInputChange}
            />
          </label>
          <br />

          <label>
            End Date:
            <input
              type="date"
              name="endDate"
              value={selectedProject.endDate || ''}
              onChange={handleInputChange}
            />
          </label>
          <br />

          <label>
            Status:
            <select
              name="status"
              value={selectedProject.status || 'in-progress'}
              onChange={handleInputChange}
            >
              <option value="in-progress">In Progress</option>
              <option value="completed">Completed</option>
              <option value="on-hold">On Hold</option>
            </select>
          </label>
          <br />

          <label>
            Findings:
            <textarea
              name="findings"
              value={selectedProject.findings || ''}
              onChange={handleInputChange}
            />
          </label>
          <br />

          <button type="submit">✅ Save</button>
        </form>
      )}
    </div>
  );
};

export default Inprogress;
