import React, { useState } from 'react';

const Settings = () => {
    const [profile, setProfile] = useState({ username: '', password: '' });
    const [account, setAccount] = useState({ email: '', loginPassword: '' });
    const [appearance, setAppearance] = useState({ theme: 'light' });

    const handleProfileChange = (e) => {
        const { name, value } = e.target;
        setProfile({ ...profile, [name]: value });
    };

    const handleAccountChange = (e) => {
        const { name, value } = e.target;
        setAccount({ ...account, [name]: value });
    };

    const handleAppearanceChange = (e) => {
        setAppearance({ theme: e.target.value });
    };

    const saveSettings = () => {
        console.log('Profile:', profile);
        console.log('Account:', account);
        console.log('Appearance:', appearance);
        alert('Settings saved successfully!');
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
            <h1>Settings</h1>
            
            <section>
                <h2>Edit Profile</h2>
                <label>
                    Username:
                    <input
                        type="text"
                        name="username"
                        value={profile.username}
                        onChange={handleProfileChange}
                    />
                </label>
                <br />
                <label>
                    Password:
                    <input
                        type="password"
                        name="password"
                        value={profile.password}
                        onChange={handleProfileChange}
                    />
                </label>
            </section>
            
            <section>
                <h2>Edit Account</h2>
                <label>
                    Email:
                    <input
                        type="email"
                        name="email"
                        value={account.email}
                        onChange={handleAccountChange}
                    />
                </label>
                <br />
                <label>
                    Login Password:
                    <input
                        type="password"
                        name="loginPassword"
                        value={account.loginPassword}
                        onChange={handleAccountChange}
                    />
                </label>
            </section>
            
            <section>
                <h2>Edit Appearance</h2>
                <label>
                    Theme:
                    <select value={appearance.theme} onChange={handleAppearanceChange}>
                        <option value="light">Light</option>
                        <option value="dark">Dark</option>
                    </select>
                </label>
            </section>
            
            <button onClick={saveSettings} style={{ marginTop: '20px', padding: '10px 20px' }}>
                Save Settings
            </button>
        </div>
    );
};

export default Settings;
