import React from 'react';
import { FaSearch, FaBell, FaUserCircle } from 'react-icons/fa';

const NavBar = () => {
    const styles = {
        navbar: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            padding: '10px 20px',
            backgroundColor: '#333',
            color: '#fff',
        },
        logo: {
            fontSize: '24px',
            fontWeight: 'bold',
            color: '#fff',
            textDecoration: 'none',
        },
        searchContainer: {
            display: 'flex',
            alignItems: 'center',
            backgroundColor: '#fff',
            borderRadius: '5px',
            padding: '5px 10px',
        },
        searchInput: {
            border: 'none',
            outline: 'none',
            marginRight: '5px',
        },
        searchIcon: {
            color: '#333',
        },
        iconsContainer: {
            display: 'flex',
            alignItems: 'center',
            gap: '15px',
            position: 'relative', // Added for dropdown positioning
        },
        icon: {
            fontSize: '30px',
            cursor: 'pointer',
        },
        Usericon: {
            fontSize: '30px',
            cursor: 'pointer',
        },
        dropdown: {
            position: 'absolute',
            top: '40px',
            right: '0',
            backgroundColor: '#fff',
            boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
            borderRadius: '5px',
            overflow: 'hidden',
            zIndex: 1000,
        },
        dropdownItem: {
            padding: '10px 15px',
            cursor: 'pointer',
            color: '#333',
            textDecoration: 'none',
            display: 'block',
            borderBottom: '1px solid #ddd',
        },
        dropdownItemLast: {
            padding: '10px 15px',
            cursor: 'pointer',
            color: '#333',
            textDecoration: 'none',
            display: 'block',
        },
    };

    return (
        <nav style={styles.navbar}>
            <a href="/" style={styles.logo}>
                <img 
                    src="\logo\android-chrome-192x192.png" 
                    alt="Logo" 
                    style={{ height: '40px', width: '40px' }} 
                />
            </a>
            <div style={styles.searchContainer}>
                <input
                    type="text"
                    placeholder="Search here"
                    style={styles.searchInput}
                />
                <FaSearch style={styles.searchIcon} />
            </div>
            <div style={styles.iconsContainer}>
                <FaBell style={styles.icon} title="Notifications" />
                <FaUserCircle style={styles.Usericon} title="User Profile" />
            </div>
        </nav>
    );
};

export default NavBar;
