import React from 'react';

const sidebarItems = [
    { name: 'Dashboard', key: 'dashboard' },
    { name: 'Projects', key: 'projects' },
    { name: 'Risk Management', key: 'risk-management' },
    { name: 'Controls', key: 'controls' },
    { name: 'Findings', key: 'findings' },
    { name: 'Reports', key: 'reports' },
    { name: 'Library', key: 'library' },
];

const Dashboard = ({ selectedKey }) => {
    const contentMap = {
        dashboard: <h2>Dashboard Overview</h2>,
        projects: <h2>Projects Management</h2>,
        'risk-management': <h2>Risk Analysis & Planning</h2>,
        controls: <h2>Control Settings</h2>,
        findings: <h2>Audit Findings</h2>,
        reports: <h2>Generated Reports</h2>,
        library: <h2>Knowledge Library</h2>,
    };

    return <div style={styles.content}>{contentMap[selectedKey] || <h2>Select a section</h2>}</div>;
};

const SideBar = () => (
    <aside style={styles.sidebar}>
        <div style={styles.header}>Menu</div>
        <ul style={styles.list}>
            {sidebarItems.map(item => (
                <li key={item.key} style={styles.listItem}>
                    {item.name}
                </li>
            ))}
        </ul>
    </aside>
);

const styles = {
    sidebar: {
        width: '220px',
        height: '100vh',
        background: '#23272f',
        color: '#fff',
        padding: '24px 0',
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
    },
    header: {
        fontSize: '1.3rem',
        fontWeight: 'bold',
        marginBottom: '32px',
        textAlign: 'center',
        letterSpacing: '1px',
    },
    list: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
        flex: 1,
    },
    listItem: {
        padding: '14px 32px',
        cursor: 'pointer',
        fontSize: '1rem',
        borderRadius: '4px',
        marginBottom: '8px',
        transition: 'background 0.2s',
    },
};

export default SideBar;
