import { useParams, useNavigate } from "react-router-dom";
import { useState } from "react";

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();
  const decodedProjectName = decodeURIComponent(projectName);

  const [expanded, setExpanded] = useState({
    client: false,
    meta: false,
    engagement: false,
    documentation: false,
  });

  // Editable states
  const [clientInfo, setClientInfo] = useState({
    name: "Example Company Ltd.",
    contact: "John Doe",
    address: "123 Main Street, Accra",
    email: "johndoe@example.com",
    phone: "+233 24 000 1111",
  });

  const [metaInfo, setMetaInfo] = useState({
    duration: "Jan 5, 2025 ‚Äì Jan 20, 2025",
    team: "Alice, Michael, Ebenezer",
  });

  const [engagementInfo, setEngagementInfo] = useState({
    scope: "Financial compliance & internal controls",
    objectives: "Ensure accurate reporting, assess risk exposure",
    risks: "Fraud, regulatory non-compliance",
  });

  // Documentation (list of uploaded files)
  const [documents, setDocuments] = useState([
    { name: "Audit Plan.pdf", url: "#" },
    { name: "Risk Assessment.xlsx", url: "#" },
    { name: "Evidence_Photos.zip", url: "#" },
  ]);

  // File upload
  const [file, setFile] = useState(null);

  const handleFileUpload = () => {
    if (!file) return;
    const newDoc = { name: file.name, url: URL.createObjectURL(file) };
    setDocuments((prev) => [...prev, newDoc]);
    setFile(null);
  };

  const toggleSection = (section) => {
    setExpanded((prev) => ({ ...prev, [section]: !prev[section] }));
  };

  // Inline file preview
  const [selectedFile, setSelectedFile] = useState(null);

  const openFile = (doc) => {
    setSelectedFile(doc);
  };

  return (
    <>
      <button onClick={() => navigate(-1)} className="back-button">
        ‚Üê Back
      </button>

      <h1 className="header">{decodedProjectName}</h1>

      <div className="project-detail-wrapper">
        {/* Client Information */}
        <section>
          <h2 onClick={() => toggleSection("client")} className="collapsible-header">
            <span className={`arrow ${expanded.client ? "down" : ""}`}>‚ñ∂</span> Client Information
          </h2>
          {expanded.client && (
            <div className="collapsible-content">
              <input
                type="text"
                value={clientInfo.name}
                onChange={(e) => setClientInfo({ ...clientInfo, name: e.target.value })}
              />
              <input
                type="text"
                value={clientInfo.contact}
                onChange={(e) => setClientInfo({ ...clientInfo, contact: e.target.value })}
              />
              <input
                type="text"
                value={clientInfo.address}
                onChange={(e) => setClientInfo({ ...clientInfo, address: e.target.value })}
              />
              <input
                type="email"
                value={clientInfo.email}
                onChange={(e) => setClientInfo({ ...clientInfo, email: e.target.value })}
              />
              <input
                type="tel"
                value={clientInfo.phone}
                onChange={(e) => setClientInfo({ ...clientInfo, phone: e.target.value })}
              />
              <button className="rename-btn">üíæ Save</button>
            </div>
          )}
        </section>

        {/* Meta Information */}
        <section>
          <h2 onClick={() => toggleSection("meta")} className="collapsible-header">
            <span className={`arrow ${expanded.meta ? "down" : ""}`}>‚ñ∂</span> Meta Information
          </h2>
          {expanded.meta && (
            <div className="collapsible-content">
              <input
                type="text"
                value={metaInfo.duration}
                onChange={(e) => setMetaInfo({ ...metaInfo, duration: e.target.value })}
              />
              <input
                type="text"
                value={metaInfo.team}
                onChange={(e) => setMetaInfo({ ...metaInfo, team: e.target.value })}
              />
              <button className="rename-btn">üíæ Save</button>
            </div>
          )}
        </section>

        {/* Audit Engagement */}
        <section>
          <h2 onClick={() => toggleSection("engagement")} className="collapsible-header">
            <span className={`arrow ${expanded.engagement ? "down" : ""}`}>‚ñ∂</span> Audit Engagement
          </h2>
          {expanded.engagement && (
            <div className="collapsible-content">
              <textarea
                value={engagementInfo.scope}
                onChange={(e) => setEngagementInfo({ ...engagementInfo, scope: e.target.value })}
              />
              <textarea
                value={engagementInfo.objectives}
                onChange={(e) => setEngagementInfo({ ...engagementInfo, objectives: e.target.value })}
              />
              <textarea
                value={engagementInfo.risks}
                onChange={(e) => setEngagementInfo({ ...engagementInfo, risks: e.target.value })}
              />
              <button className="rename-btn">üíæ Save</button>
            </div>
          )}
        </section>

        {/* Documentation */}
        <section>
          <h2 onClick={() => toggleSection("documentation")} className="collapsible-header">
            <span className={`arrow ${expanded.documentation ? "down" : ""}`}>‚ñ∂</span> Documentation
          </h2>
          {expanded.documentation && (
            <div className="collapsible-content">
              <ul className="documents-list">
                {documents.map((doc, idx) => (
                  <li key={idx}>
                    <button onClick={() => openFile(doc)}>{doc.name}</button>
                  </li>
                ))}
              </ul>
              <div className="upload-section">
                <input type="file" onChange={(e) => setFile(e.target.files[0])} />
                <button className="rename-btn" onClick={handleFileUpload}>Upload</button>
              </div>
            </div>
          )}
        </section>

        {/* File Preview */}
        {selectedFile && (
          <div className="file-preview">
            <h3>Preview: {selectedFile.name}</h3>
            {selectedFile.name.endsWith(".png") ||
            selectedFile.name.endsWith(".jpg") ||
            selectedFile.name.endsWith(".jpeg") ? (
              <img src={selectedFile.url} alt={selectedFile.name} style={{ maxWidth: "100%" }} />
            ) : selectedFile.name.endsWith(".pdf") ? (
              <iframe src={selectedFile.url} width="100%" height="500px" title="PDF Preview"></iframe>
            ) : (
              <p>‚ö†Ô∏è File preview not supported, but you can download it.</p>
            )}
            <button onClick={() => setSelectedFile(null)}>Close Preview</button>
          </div>
        )}
      </div>
    </>
  );
};

export default ProjectDetail;
