import React from 'react';

const styles = {
    container: {
        padding: '32px',
        fontFamily: 'Segoe UI, Arial, sans-serif',
        backgroundColor: '#f4f6f9',
        minHeight: '100vh',
        width: '100%',
        boxSizing: 'border-box',
    },
    header: {
        display: 'flex',
        alignItems: 'center',
        borderBottom: '2px solid #1a3a6b',
        paddingBottom: '16px',
        marginBottom: '32px',
    },
    logo: {
        width: '48px',
        marginRight: '16px',
    },
    title: {
        fontSize: '2rem',
        fontWeight: '600',
        color: '#1a3a6b',
        letterSpacing: '0.5px',
    },
    grid: {
        display: 'flex',
        justifyContent: 'space-around',
        flexWrap: 'wrap',
        gap: '32px',
    },
    card: {
        width: '160px',
        height: '160px',
        borderRadius: '16px',
        backgroundColor: '#fff',
        boxShadow: '0 4px 12px rgba(26,58,107,0.08)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        cursor: 'pointer',
        textAlign: 'center',
        transition: 'transform 0.2s, box-shadow 0.2s',
        border: '2px solid #e3eaf5',
        outline: 'none',
    },
    cardActive: {
        transform: 'scale(1.05)',
        boxShadow: '0 8px 24px rgba(26,58,107,0.15)',
        border: '2px solid #1a3a6b',
    },
    icon: {
        fontSize: '2.5rem',
        color: '#1a3a6b',
        marginBottom: '12px',
    },
    label: {
        fontSize: '1.05rem',
        fontWeight: '500',
        color: '#274e8e',
        marginBottom: '6px',
    },
    value: {
        fontSize: '1.4rem',
        fontWeight: 'bold',
        color: '#007BFF',
    },
};

const dashboardItems = [
    { label: 'Audit Status Overview', icon: '📊', value: '12 Active' },
    { label: 'Recent Activity', icon: '🕒', value: '5 Updates' },
    { label: 'Due Reports', icon: '📅', value: '3 Due' },
    { label: 'NC Reports', icon: '⚠️', value: '2 Open' },
    { label: 'KPIs', icon: '📈', value: '7 Metrics' },
];

const Dashboard = () => {
    const [activeIndex, setActiveIndex] = React.useState(null);

    const handleCardClick = (index) => {
        // Placeholder for navigation or modal
        setActiveIndex(index);
        // Example: navigate(`/dashboard/${dashboardItems[index].label.toLowerCase().replace(/ /g, '-')}`);
    };

    return (
        <div style={styles.container}>
            <div style={styles.header}>
                <img
                    src="/logo/android-chrome-192x192.png"
                    alt="NexA Audit Logo"
                    style={styles.logo}
                />
                <span style={styles.title}>NexA Audit Portal</span>
            </div>
            <div style={styles.grid}>
                {dashboardItems.map((item, idx) => (
                    <div
                        key={item.label}
                        style={{
                            ...styles.card,
                            ...(activeIndex === idx ? styles.cardActive : {}),
                        }}
                        tabIndex={0}
                        role="button"
                        aria-label={item.label}
                        onClick={() => handleCardClick(idx)}
                        onKeyPress={(e) => {
                            if (e.key === 'Enter' || e.key === ' ') handleCardClick(idx);
                        }}
                        onMouseEnter={() => setActiveIndex(idx)}
                        onMouseLeave={() => setActiveIndex(null)}
                    >
                        <span style={styles.icon}>{item.icon}</span>
                        <span style={styles.label}>{item.label}</span>
                        <span style={styles.value}>{item.value}</span>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Dashboard;
