import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AllProjects = () => {
  const [projects, setProjects] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const res = await fetch('http://localhost:2000/api/projects/all-projects');
        const data = await res.json();
        if (data.projects) {
          setProjects(data.projects);
        }
      } catch (err) {
        console.error('‚ùå Failed to fetch projects:', err);
      }
    };

    fetchProjects();
  }, []);

  return (
    <>
      {/* ‚úÖ Back Button */}
      <button onClick={() => navigate('/projects')} className="back-button">
        ‚Üê Back
      </button>

      <h1 className="header">Project List</h1>

      {/* üîΩ List of Projects */}
     <ul className="project-list">
  {projects.map((project, index) => {
    const projectData = project.AllProjects || project; // if nested

    return (
      <li key={index} className="project-item">
        <div><strong>Project Name:</strong> {projectData.projectName}</div>
        <div><strong>Client:</strong> {projectData.clientName}</div>
        <div><strong>Status:</strong> {projectData.status}</div>
        {/* Add more fields as needed */}
      </li>
    );
  })}
</ul>

    </>
  );
};

export default AllProjects;
