import { useParams, useNavigate } from 'react-router-dom';
import { useState, useCallback } from 'react';

const ProjectDetail = () => {
  const { projectName } = useParams();
  const navigate = useNavigate();

  const decodedName = decodeURIComponent(projectName);
  const [newName, setNewName] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const notify = (message) => alert(message); // Replace with toast if needed

  // Upload File
  const handleFileUpload = useCallback(async () => {
    if (!file) return notify('Please select a file.');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('projectName', decodedName);

    try {
      setLoading(true);
      const res = await fetch('http://localhost:2000/api/projects/upload', {
        method: 'POST',
        body: formData,
      });

      res.ok
        ? notify('‚úÖ File uploaded successfully!')
        : notify('‚ùå File upload failed.');
    } catch (err) {
      console.error('Upload error:', err);
      notify('‚ùå An error occurred while uploading.');
    } finally {
      setLoading(false);
    }
  }, [file, decodedName]);

  // Rename Project
  const handleRename = useCallback(async () => {
    if (!newName.trim()) return notify('New project name cannot be empty.');

    try {
      setLoading(true);
      const res = await fetch('http://localhost:2000/api/projects/rename', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ oldName: decodedName, newName }),
      });

      if (res.ok) {
        notify('‚úÖ Project renamed!');
        navigate(`/projects/${encodeURIComponent(newName)}`);
      } else {
        notify('‚ùå Rename failed.');
      }
    } catch (err) {
      console.error('Rename error:', err);
      notify('‚ùå An error occurred during renaming.');
    } finally {
      setLoading(false);
    }
  }, [newName, decodedName, navigate]);

  // Delete Project
  const handleDelete = useCallback(async () => {
    const confirmDelete = window.confirm('Are you sure you want to delete this project?');
    if (!confirmDelete) return;

    try {
      setLoading(true);
      const res = await fetch(`http://localhost:2000/api/projects/delete/${encodeURIComponent(decodedName)}`, {
        method: 'DELETE',
      });

      if (res.ok) {
        notify('üóëÔ∏è Project deleted.');
        navigate('/projects');
      } else {
        notify('‚ùå Delete failed.');
      }
    } catch (err) {
      console.error('Delete error:', err);
      notify('‚ùå An error occurred while deleting.');
    } finally {
      setLoading(false);
    }
  }, [decodedName, navigate]);

  // Share Link
  const handleShare = useCallback(() => {
    const shareLink = `${window.location.origin}/projects/${encodeURIComponent(decodedName)}`;
    navigator.clipboard.writeText(shareLink);
    notify('üì§ Shareable link copied to clipboard!');
  }, [decodedName]);

  return (
    <div className="project-detail-container">
      <button onClick={() => navigate(-1)} className="back-button">
        ‚Üê Back
      </button>

      <h1 className="project-title">{decodedName}</h1>

      <section className="actions-section">
        <p>Manage your project:</p>
        <ul className="action-list">
          <li>
            <label>
              üìé Attach a file:
              <input type="file" onChange={(e) => setFile(e.target.files?.[0])} />
            </label>
            <button onClick={handleFileUpload} disabled={loading}>Upload</button>
          </li>

          <li>
            <label>
              ‚úèÔ∏è Rename project:
              <input
                type="text"
                placeholder="New name"
                value={newName}
                onChange={(e) => setNewName(e.target.value)}
              />
            </label>
            <button onClick={handleRename} disabled={loading}>Rename</button>
          </li>

          <li>
            üóëÔ∏è Delete this project:
            <button onClick={handleDelete} disabled={loading} className="delete-button">
              Delete
            </button>
          </li>

          <li>
            üì§ Share:
            <button onClick={handleShare}>Copy Shareable Link</button>
          </li>
        </ul>
      </section>
    </div>
  );
};

export default ProjectDetail;
