import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Outlet } from 'react-router-dom';
import { Auth0Provider, useAuth0 } from '@auth0/auth0-react';

import Dashboard from './frontend/SideBar/Dashboard/Dashboard';
import NavBar from './frontend/NavBar/NavBar';
import LoginPage from './frontend/LoginPage';
import SideBar from './frontend/SideBar/SideBar';
import Projects from './frontend/SideBar/Projects/Projects';
import Reports from './frontend/SideBar/Reports/reports';
import ProfilePage from './frontend/NavBar/ProfilePage';
import Logout from './frontend/NavBar/Logout';
import Settings from './frontend/NavBar/Settings';
import Library from './frontend/SideBar/Library/Library';
import AllProjects from './frontend/SideBar/Projects/AllProjects';
import Completed from './frontend/SideBar/Projects/Completed';
import Inprogress from './frontend/SideBar/Projects/InProgress';
import NotCompleted from './frontend/SideBar/Projects/NotCompleted';
import ProjectDetail from './frontend/SideBar/Projects/ProjectDetails';
import CompletedReports from './frontend/SideBar/Reports/completedreports';
import InprogressReports from './frontend/SideBar/Reports/inprogressreports';
import DueReports from './frontend/SideBar/Reports/duereport';
import ReportsList from './frontend/SideBar/Reports/reportslist';
import './app.css';

/**
 * PrivateRoute component:
 * - While loading auth state: shows a simple loading placeholder.
 * - If unauthenticated: triggers loginWithRedirect (Auth0 handles MFA if enabled).
 * - If authenticated: renders children.
 */
const PrivateRoute = ({ children }) => {
  const { isAuthenticated, isLoading, loginWithRedirect } = useAuth0();

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      // Redirect the user to Auth0 login page (Auth0 will handle MFA)
      loginWithRedirect();
    }
  }, [isLoading, isAuthenticated, loginWithRedirect]);

  if (isLoading) {
    // Replace with a nicer loader if you have one
    return <div style={{ padding: 24 }}>Loading authenticationâ€¦</div>;
  }

  // While loginWithRedirect will redirect unauthenticated users,
  // return null here to avoid rendering protected UI briefly.
  if (!isAuthenticated) return null;

  return children;
};

/**
 * ProtectedLayout:
 * Used to wrap all protected routes with NavBar and SideBar layout.
 * It renders <Outlet /> where nested routes are shown.
 */
const ProtectedLayout = () => (
  <>
    <NavBar />
    <SideBar />
    <div className="wrapper">
      <div className="container">
        <Outlet />
      </div>
    </div>
  </>
);

function AppRoutes() {
  return (
    <Routes>
      {/* Public Route: Login */}
      <Route path="/" element={<LoginPage />} />

      {/* Protected routes: wrapped by PrivateRoute + ProtectedLayout */}
      <Route
        element={
          <PrivateRoute>
            <ProtectedLayout />
          </PrivateRoute>
        }
      >
        {/* These are nested under the layout via Outlet */}
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/projects" element={<Projects />} />
        <Route path="/reports" element={<Reports />} />
        <Route path="/library" element={<Library />} />
        <Route path="/profile" element={<ProfilePage />} />
        <Route path="/logout" element={<Logout />} />
        <Route path="/settings" element={<Settings />} />
        <Route path="/all-projects" element={<AllProjects />} />
        <Route path="/completed" element={<Completed />} />
        <Route path="/in-progress" element={<Inprogress />} />
        <Route path="/not-completed" element={<NotCompleted />} />
        <Route path="/projects/completed/:projectName" element={<ProjectDetail />} />
        <Route path="/projects/not-completed/:projectName" element={<ProjectDetail />} />
        <Route path="/projects/in-progress/:projectName" element={<ProjectDetail />} />
        <Route path="/completed-reports" element={<CompletedReports />} />
        <Route path="/in-progress-reports" element={<InprogressReports />} />
        <Route path="/due-reports" element={<DueReports />} />
        <Route path="/reports-list" element={<ReportsList />} />
      </Route>

      {/* Catch-all: redirect to / for unknown routes */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
}

function App() {
  // Auth0 configuration from environment variables
  const domain = process.env.REACT_APP_AUTH0_DOMAIN;
  const clientId = process.env.REACT_APP_AUTH0_CLIENT_ID;

  if (!domain || !clientId) {
    // Fail fast so you can notice missing env vars during development
    return (
      <div style={{ padding: 24 }}>
        <h2>Auth0 configuration missing</h2>
        <p>
          Please set <code>REACT_APP_AUTH0_DOMAIN</code> and <code>REACT_APP_AUTH0_CLIENT_ID</code> in your environment.
        </p>
      </div>
    );
  }

  return (
    <Auth0Provider
      domain={domain}
      clientId={clientId}
      authorizationParams={{
        // Redirect back to the app root (adjust if you need a specific callback path)
        redirect_uri: window.location.origin,
      }}
      // optional: you can set cacheLocation="localstorage" if you need SPA token persistence across tabs,
      // but prefer the default in-memory storage unless you know the implications.
    >
      <Router>
        <div className="App">
          <AppRoutes />
        </div>
      </Router>
    </Auth0Provider>
  );
}

export default App;
