import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Inprogress = () => {

  const navigate = useNavigate();

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const res = await fetch('http://localhost:2000/api/projects/in-progress');
        const data = await res.json();
        if (data.projects) {
          setProjects(data.projects);
        }
      } catch (err) {
        console.error('Failed to fetch projects:', err);
      }
    };

    fetchProjects();
  }, []);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:2000/api/projects', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const newProject = await res.json();
      if (newProject) {
        setProjects((prev) => [...prev, newProject]);
        setFormData({
          projectId: '',
          projectName: '',
          clientName: '',
          auditType: '',
          auditor: '',
          startDate: '',
          expectedEndDate: '',
          status: 'in-progress',
          notes: '',
        });
      }
    } catch (err) {
      console.error('Error submitting form:', err);
    }
  };

  return (
    <>
      <button onClick={() => navigate('/projects')} className="back-button">
        ‚Üê Back
      </button>

      <h2 className='header'>Audits In-Progress</h2>

      <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
        {projects.map((project, index) => (
          <li key={index} styles={{ marginBottom: '1.5rem', borderBottom: '1px solid #ccc', paddingBottom: '1rem' }}>
            {Object.entries(project).map(([key, value]) => (
              <div key={key}>
                <strong>{key}:</strong> {value?.toString()}
              </div>
            ))}
          </li>
        ))}
      </ul>
    </>
  );
};

export default Inprogress;
